// Get current values
const roomTemp = flow.get('room_temp') || msg.room_temp || parseFloat(msg.payload);
const coilTemp = flow.get('coil_temp') || msg.coil_temp || parseFloat(msg.payload);
const targetTemp = flow.get('target_temp') || 30;
const hysteresis = flow.get('hysteresis') || 2;
const coilFreezeThreshold = flow.get('coil_freeze_threshold') || 33;
const coilResumeThreshold = flow.get('coil_resume_threshold') || 36;
const acStatus = flow.get('ac_status') || 'off';
const lastOffTime = flow.get('last_off_time') || 0;
const minOffTimeMs = (flow.get('min_off_time_minutes') || 8) * 60 * 1000;
const acStartTime = flow.get('ac_start_time') || 0;
const maxRuntimeMs = (flow.get('max_runtime_hours') || 4) * 60 * 60 * 1000;

// Safety checks
if (isNaN(roomTemp) || isNaN(coilTemp)) {
    flow.set('system_status', 'sensor_error');
    node.warn('Sensor reading error - entering safe mode');
    return [null, { payload: 'off', reason: 'sensor_error' }];
}

// Check if minimum off time has passed
const timeSinceOff = Date.now() - lastOffTime;
const canTurnOn = timeSinceOff > minOffTimeMs;

// Check if AC has been running too long
const runtimeMs = acStatus === 'on' ? Date.now() - acStartTime : 0;
const runtimeExceeded = runtimeMs > maxRuntimeMs;

let newStatus = acStatus;
let reason = 'normal_operation';

// Decision logic
if (acStatus === 'off') {
    // AC is currently off - check if we should turn it on
    if (roomTemp > (targetTemp + hysteresis) && 
        coilTemp > coilResumeThreshold && 
        canTurnOn) {
        newStatus = 'on';
        reason = 'cooling_needed';
        flow.set('ac_start_time', Date.now());
    }
} else {
    // AC is currently on - check if we should turn it off
    if (coilTemp <= coilFreezeThreshold) {
        newStatus = 'off';
        reason = 'freeze_protection';
        flow.set('last_off_time', Date.now());
    } else if (roomTemp <= targetTemp) {
        newStatus = 'off';
        reason = 'target_reached';
        flow.set('last_off_time', Date.now());
    } else if (runtimeExceeded) {
        newStatus = 'off';
        reason = 'max_runtime_exceeded';
        flow.set('last_off_time', Date.now());
    }
}

// Update status
flow.set('ac_status', newStatus);
flow.set('system_status', reason);
flow.set('room_temp', roomTemp);
flow.set('coil_temp', coilTemp);

// Create status message
const statusMsg = {
    payload: {
        room_temp: roomTemp,
        coil_temp: coilTemp,
        target_temp: targetTemp,
        ac_status: newStatus,
        reason: reason,
        can_turn_on: canTurnOn,
        runtime_hours: runtimeMs / (1000 * 60 * 60),
        time_since_off_minutes: timeSinceOff / (1000 * 60)
    }
};

// Output to AC control if status changed
const controlMsg = newStatus !== acStatus ? 
    { payload: newStatus, reason: reason } : null;

return [statusMsg, controlMsg];

if (!msg.payload) return null;

const command = msg.payload === 'on' ? 'turn_on' : 'turn_off';
const reason = msg.reason || 'unknown';

// Log the action
node.log(`AC ${command} - Reason: ${reason}`);

// Send command to smart plug
const plugMsg = {
    payload: {
        domain: 'switch',
        service: command,
        data: {
            entity_id: 'switch.ac_plug'
        }
    },
    reason: reason
};

// Also control heating pad if enabled
const heatingPadEnabled = flow.get('enable_heating_pad') || false;
const heatingPadMsg = heatingPadEnabled ? {
    payload: {
        domain: 'switch',
        service: msg.payload === 'on' ? 'turn_on' : 'turn_off',
        data: {
            entity_id: 'switch.heating_pad'
        }
    }
} : null;

return [plugMsg, heatingPadMsg];

const irEnabled = flow.get('enable_ir_control') || false;

if (!irEnabled) {
    return null;
}

// Send IR command to set AC to cool mode, 60Â°F, high fan
return {
    payload: {
        domain: 'remote',
        service: 'send_command',
        data: {
            entity_id: 'remote.ac_ir_blaster',
            command: ['cool_60_high']
        }
    }
};

const data = msg.payload;

// Format for dashboard display
const status = {
    room_temp: Math.round(data.room_temp * 10) / 10,
    coil_temp: Math.round(data.coil_temp * 10) / 10,
    target_temp: data.target_temp,
    ac_status: data.ac_status,
    status_text: formatStatusText(data.reason),
    runtime_hours: Math.round(data.runtime_hours * 100) / 100,
    can_turn_on: data.can_turn_on,
    timestamp: new Date().toLocaleString()
};

function formatStatusText(reason) {
    switch(reason) {
        case 'cooling_needed': return 'Cooling';
        case 'freeze_protection': return 'Freeze Protection';
        case 'target_reached': return 'Target Reached';
        case 'max_runtime_exceeded': return 'Max Runtime';
        case 'sensor_error': return 'Sensor Error';
        case 'normal_operation': return 'Monitoring';
        default: return 'Unknown';
    }
}

return { payload: status };

// Split status for different dashboard elements
const status = msg.payload;

// Temperature gauges
const tempMsg = {
    topic: "temperatures",
    payload: {
        room: status.room_temp,
        coil: status.coil_temp,
        target: status.target_temp
    }
};

// Status text
const statusMsg = {
    topic: "status",
    payload: status.status_text,
    ac_on: status.ac_status === 'on'
};

// Runtime info
const runtimeMsg = {
    topic: "runtime", 
    payload: {
        hours: status.runtime_hours,
        can_start: status.can_turn_on,
        timestamp: status.timestamp
    }
};

return [tempMsg, statusMsg, runtimeMsg];

// Initialize flow variables with default values
flow.set('target_temp', 30);
flow.set('hysteresis', 2);
flow.set('coil_freeze_threshold', 33);
flow.set('coil_resume_threshold', 36);
flow.set('min_off_time_minutes', 8);
flow.set('max_runtime_hours', 4);
flow.set('enable_heating_pad', true);
flow.set('enable_ir_control', false);
flow.set('ac_status', 'off');
flow.set('system_status', 'initializing');
flow.set('last_off_time', 0);
flow.set('ac_start_time', 0);

node.log('CoolBot system initialized with default settings');

return msg;
