// Check sensor timestamps and values
const roomTempLastUpdate = flow.get('room_temp_last_update') || 0;
const coilTempLastUpdate = flow.get('coil_temp_last_update') || 0;
const currentTime = Date.now();
const sensorTimeoutMs = 10 * 60 * 1000; // 10 minutes

// Check if sensors are offline
const roomSensorOffline = (currentTime - roomTempLastUpdate) > sensorTimeoutMs;
const coilSensorOffline = (currentTime - coilTempLastUpdate) > sensorTimeoutMs;

let alerts = [];
let safetyAction = null;

if (roomSensorOffline) {
    alerts.push('Room temperature sensor offline');
    flow.set('room_sensor_status', 'offline');
} else {
    flow.set('room_sensor_status', 'online');
}

if (coilSensorOffline) {
    alerts.push('Coil temperature sensor offline');
    flow.set('coil_sensor_status', 'offline');
} else {
    flow.set('coil_sensor_status', 'online');
}

// If critical sensors are offline, activate safety mode
if (roomSensorOffline || coilSensorOffline) {
    flow.set('safety_mode', true);
    safetyAction = {
        payload: {
            domain: 'switch',
            service: 'turn_off',
            data: {
                entity_id: 'switch.ac_plug'
            }
        },
        reason: 'sensor_offline'
    };
    node.warn(`Safety mode activated: ${alerts.join(', ')}`);
} else {
    flow.set('safety_mode', false);
}

// Check sensor values for reasonableness
const roomTemp = flow.get('room_temp');
const coilTemp = flow.get('coil_temp');

if (roomTemp && (roomTemp < -10 || roomTemp > 120)) {
    alerts.push(`Room temperature out of range: ${roomTemp}°F`);
}

if (coilTemp && (coilTemp < -20 || coilTemp > 100)) {
    alerts.push(`Coil temperature out of range: ${coilTemp}°F`);
}

const alertMsg = alerts.length > 0 ? {
    payload: {
        alerts: alerts,
        timestamp: new Date().toLocaleString(),
        severity: (roomSensorOffline || coilSensorOffline) ? 'critical' : 'warning'
    }
} : null;

return [alertMsg, safetyAction];

if (!msg.payload) return null;

const alerts = msg.payload.alerts;
const severity = msg.payload.severity;

// Log alerts
alerts.forEach(alert => {
    if (severity === 'critical') {
        node.error(alert);
    } else {
        node.warn(alert);
    }
});

// Store latest alerts
flow.set('latest_alerts', alerts);
flow.set('alert_timestamp', msg.payload.timestamp);

// Create notification message
return {
    payload: {
        title: `CoolBot ${severity.toUpperCase()} Alert`,
        message: alerts.join('\n'),
        severity: severity
    }
};

const acStatus = flow.get('ac_status') || 'off';
const acStartTime = flow.get('ac_start_time') || 0;
const maxRuntimeMs = (flow.get('max_runtime_hours') || 4) * 60 * 60 * 1000;
const currentTime = Date.now();

if (acStatus === 'on' && acStartTime > 0) {
    const runtimeMs = currentTime - acStartTime;
    const runtimeHours = runtimeMs / (1000 * 60 * 60);

    // Check if approaching max runtime (90% threshold)
    const warningThreshold = maxRuntimeMs * 0.9;

    if (runtimeMs > warningThreshold && runtimeMs < maxRuntimeMs) {
        const remainingMinutes = (maxRuntimeMs - runtimeMs) / (1000 * 60);

        return [{
            payload: {
                type: 'runtime_warning',
                message: `AC approaching max runtime. ${Math.round(remainingMinutes)} minutes remaining.`,
                runtime_hours: Math.round(runtimeHours * 100) / 100
            }
        }, null];
    } else if (runtimeMs > maxRuntimeMs) {
        // Force shutdown for safety
        flow.set('ac_status', 'off');
        flow.set('last_off_time', currentTime);

        return [{
            payload: {
                type: 'runtime_exceeded',
                message: `AC forced shutdown after ${Math.round(runtimeHours * 100) / 100} hours`,
                runtime_hours: runtimeHours
            }
        }, {
            payload: {
                domain: 'switch',
                service: 'turn_off',
                data: {
                    entity_id: 'switch.ac_plug'
                }
            },
            reason: 'max_runtime_exceeded'
        }];
    }
}

return [null, null];

if (!msg.payload) return null;

const alertType = msg.payload.type;
const message = msg.payload.message;

if (alertType === 'runtime_exceeded') {
    node.error(message);
} else {
    node.warn(message);
}

return {
    payload: {
        title: 'CoolBot Runtime Alert',
        message: message,
        severity: alertType === 'runtime_exceeded' ? 'critical' : 'warning'
    }
};

const roomTemp = flow.get('room_temp');
const coilTemp = flow.get('coil_temp');
const acStatus = flow.get('ac_status') || 'off';

let alerts = [];
let emergencyShutoff = false;

// Critical coil freeze check
if (coilTemp && coilTemp < 30) {
    alerts.push(`CRITICAL: Coil temperature dangerously low (${coilTemp}°F)`);
    emergencyShutoff = true;
}

// Extreme room temperature check
if (roomTemp && roomTemp < 20) {
    alerts.push(`WARNING: Room temperature extremely low (${roomTemp}°F)`);
}

// Check for rapid temperature drops
const prevCoilTemp = flow.get('prev_coil_temp') || coilTemp;
if (coilTemp && prevCoilTemp && (prevCoilTemp - coilTemp) > 5) {
    alerts.push(`WARNING: Rapid coil temperature drop detected`);
}

// Store previous temperature for next check
flow.set('prev_coil_temp', coilTemp);

if (alerts.length === 0) {
    return [null, null];
}

const alertMsg = {
    payload: {
        alerts: alerts,
        temperatures: {
            room: roomTemp,
            coil: coilTemp
        },
        severity: emergencyShutoff ? 'critical' : 'warning',
        timestamp: new Date().toLocaleString()
    }
};

const shutoffMsg = emergencyShutoff ? {
    payload: {
        domain: 'switch',
        service: 'turn_off',
        data: {
            entity_id: 'switch.ac_plug'
        }
    },
    reason: 'critical_temperature'
} : null;

if (emergencyShutoff) {
    flow.set('ac_status', 'off');
    flow.set('emergency_mode', true);
    flow.set('last_off_time', Date.now());
}

return [alertMsg, shutoffMsg];

if (!msg.payload) return null;

const alerts = msg.payload.alerts;
const severity = msg.payload.severity;
const temps = msg.payload.temperatures;

// Log each alert
alerts.forEach(alert => {
    if (severity === 'critical') {
        node.error(alert);
    } else {
        node.warn(alert);
    }
});

return {
    payload: {
        title: `CoolBot Temperature ${severity.toUpperCase()}`,
        message: alerts.join('\n') + `\n\nRoom: ${temps.room}°F, Coil: ${temps.coil}°F`,
        severity: severity
    }
};

if (!msg.payload) return null;

const notification = msg.payload;

// Store in flow for dashboard display
flow.set('latest_notification', notification);
flow.set('notification_count', (flow.get('notification_count') || 0) + 1);

// Format for different outputs
const logMsg = {
    payload: `[${notification.severity.toUpperCase()}] ${notification.title}: ${notification.message}`
};

// Create Home Assistant notification
const haNotification = {
    payload: {
        domain: 'notify',
        service: 'persistent_notification',
        data: {
            title: notification.title,
            message: notification.message,
            notification_id: `coolbot_${Date.now()}`
        }
    }
};

return [logMsg, haNotification];

// This function should be called whenever sensor data is received
// to update the last seen timestamps for the watchdog

if (msg.topic === 'room_temp') {
    flow.set('room_temp_last_update', Date.now());
} else if (msg.topic === 'coil_temp') {
    flow.set('coil_temp_last_update', Date.now());
}

return msg;
